html, body {
  margin: 0;
  padding: 0;
}

class Snake {
  constructor() {
    this.body = [];
    this.body[0] = createVector(floor(w / 2), floor(h / 2));
    this.body[1] = createVector(floor(w / 2), floor(h / 2));
    this.xdir = 0;
    this.ydir = 0;
    this.len = 0;
    this.r = 0; // Default color
    this.g = 0; // Default color
    this.b = 0; // Default color
  }

  setDir(x, y) {
    this.xdir = x;
    this.ydir = y;
  }

  update() {
    let head = this.body[this.body.length - 1].copy();
    head.x += this.xdir;
    head.y += this.ydir;

    if (head.x < 0 || head.x >= w || head.y < 0 || head.y >= h) {
      this.xdir = 0;
      this.ydir = 0;
      return; // Exit the function to prevent further updates
    }
    this.body.shift();
    this.body.push(head);
  }

  grow() {
    let head = this.body[this.body.length - 1].copy();
    this.len++;
    this.body.push(head);
  }

  eat(pos) {
    let x = this.body[this.body.length - 1].x;
    let y = this.body[this.body.length - 1].y;
    if (x == pos.x && y == pos.y) {
      this.colorUpdate();
      return true;
    }
    return false;
  }

  colorUpdate() {
    this.r = 255;
    this.g = 0;
    this.b = 255;
  }

    stop(){
    this.xdir = 0;
    this.ydir = 0;
    return; // Exit the function to prevent further updates
  }

  show() {
    for (let i = 0; i < this.body.length; i++) {
      if (i === 1) {
        // This is the head of the snake
        rectMode(CENTER);
        fill(0,0,0);
        rect(this.body[i].x, this.body[i].y, 1, 1);
        fill(this.r, this.g, this.b);

        rect(this.body[i].x, this.body[i].y, 0.93, 0.93);
      } else if(i===0){
        rectMode(CENTER);
        fill(this.r, this.g, this.b);
        noStroke();
        rect(this.body[i].x, this.body[i].y, 1, 1);
      }

    }
  }
}

// Daniel Shiffman
// http://youtube.com/thecodingtrain
// http://codingtra.in

// Coding Challenge #115: Snake Game Redux
// https://youtu.be/OMoVcohRgZA

let snake;
let rez = 20;
let food;
let w;
let h;
let r = 255;
let g = 0;
let b = 0;

function setup() {
  createCanvas(400, 400);
  background(220);
  w = floor(width / rez);
  h = floor(height / rez);
  frameRate(5);
  snake = new Snake();

  foodLocation();

  saveButton = createButton("Save Design");
  saveButton.position(150, height + 10);
  saveButton.mousePressed(saveDesign);
}

function foodLocation() {
  let x = floor(random(w));
  let y = floor(random(h));
  food = createVector(x, y);
}

function keyPressed() {
  if (keyCode === LEFT_ARROW) {
    snake.setDir(-1, 0);
  } else if (keyCode === RIGHT_ARROW) {
    snake.setDir(1, 0);
  } else if (keyCode === DOWN_ARROW) {
    snake.setDir(0, 1);
  } else if (keyCode === UP_ARROW) {
    snake.setDir(0, -1);
  } else if (key == " ") {
    snake.grow();
  }
}

function draw() {
  scale(rez);
  translate(0.5, 0.5);

  if (snake.eat(food)) {
    snake.r = r;
    snake.g = g;
    snake.b = b;
    foodLocation();
    r = random(0, 255);
    g = random(0, 255);
    b = random(0, 255);
  }
  snake.update();
  snake.show();

  noStroke();
  fill(0,0,0);
  rect(food.x, food.y, 1, 1);
  fill(r, g, b);
  rect(food.x, food.y, 0.9, 0.9);
}

function saveDesign() { 
  fill(r, g, b);
  rect(food.x, food.y, 1, 1);
  for (let segment of snake.body) {
    fill(snake.r, snake.g, snake.b); // Use the snake's color
    rect(segment.x, segment.y, 1, 1);
  }
  saveCanvas("SnakeGame", "jpg");
}

function mousePressed() {
    snake.stop();
}

