  image(video, width / 2, height / 2, width - 230, height);
  // background(0);
  // // tint(255, 10); 
  image(womb, width / 2 - 140 , height / 2, height - 80, height - 80);
  stroke(8);

  fill(0, 0, 0, pulsing);
  square(-width, -height, width * 10);  

  for (let i = circles.length - 1; i >= 0; i--) {
    let obj = circles[i];
    obj.display();
    // text(obj.x, width / 2, height / 2 - 50);
